import React from 'react';
import CartItem from './CartItem';

const Cart = ({ cartItems, removeFromCart, closeCartModal }) => {
  const total = cartItems.reduce((acc, item) => acc + item.price, 0);

  return (
    <div style={{position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', background: '#fff', padding: '50px'}}>
      <h2>Cart</h2>
      {cartItems.map((item) => (
        <CartItem key={item.uniqueId} item={item} removeFromCart={removeFromCart} />
      ))}
      <p>Total: ${total.toFixed(2)}</p>
      <button onClick={closeCartModal}>Close</button>
    </div>
  );
};

export default Cart;
