import React, { useState } from 'react';
import './App.css';
import ProductList from './ProductList';
import Cart from './Cart';
import CartModal from './CartModal'; // import the new CartModal component

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [isCartOpen, setCartOpen] = useState(false); // new state for managing if the cart is open or not

  const addToCart = (product) => {
    setCartItems(currentCartItems => [...currentCartItems, product]);
  }

  const toggleCart = () => {
    setCartOpen(!isCartOpen); // toggle the cart open state
  }

  return (
    <div className="App">
      <button onClick={toggleCart}>View Cart</button> {/* new button for opening the cart */}
      <ProductList onAddToCart={addToCart} />
      <CartModal isOpen={isCartOpen} onClose={toggleCart}> {/* use the CartModal component */}
        <Cart items={cartItems} />
      </CartModal>
    </div>
  );
}

export default App;
