import React, { useState } from 'react';
import './App.css';
import ProductList from './ProductList';
import Cart from './Cart';
import CartModal from './CartModal'; // новый импорт

function App() {
  const [products, setProducts] = useState([]); // Ваше текущее состояние
  const [cartItems, setCartItems] = useState([]);
  const [isCartOpen, setCartOpen] = useState(false); // новое состояние для управления открытием корзины

  // Ваша текущая функция
  const fetchProducts = async () => {
    // код для получения продуктов
  }

  const addToCart = (product) => {
    setCartItems(currentCartItems => [...currentCartItems, product]);
  }

  const toggleCart = () => {
    setCartOpen(!isCartOpen); // переключение состояния открытой корзины
  }

  // Ваш текущий useEffect
  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className="App">
      <button onClick={toggleCart}>View Cart</button> {/* новая кнопка для открытия корзины */}
      <ProductList products={products} onAddToCart={addToCart} />
      <CartModal isOpen={isCartOpen} onClose={toggleCart}> {/* использование компонента CartModal */}
        <Cart items={cartItems} />
      </CartModal>
    </div>
  );
}

export default App;
