import React, { useState, useEffect } from 'react';
import ProductList from './ProductList';
import Cart from './Cart';

const App = () => {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const response = await fetch('https://fakestoreapi.com/products');
    const data = await response.json();
    setProducts(data);
  };

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
    console.log(cartItems); // Log the state of cartItems
  };

  const removeFromCart = (product) => {
    setCartItems(cartItems.filter((item) => item.id !== product.id));
  };

  const toggleCart = () => {
    setShowCart(!showCart);
    console.log(showCart); // Log the state of showCart
  };

  return (
    <div>
      <button onClick={toggleCart}>Toggle Cart</button>
      <ProductList products={products} addToCart={addToCart} />
      {showCart && <Cart cartItems={cartItems} removeFromCart={removeFromCart} />}
    </div>
  );
};

export default App;
