import React from 'react';
import './App.css';
import Product from './Product';
import Cart from './Cart';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [
        {id: 1, name: 'Product 1', description: 'Description 1', price: 10, image: 'https://via.placeholder.com/250'},
        {id: 2, name: 'Product 2', description: 'Description 2', price: 20, image: 'https://via.placeholder.com/250'},
        // Add more products as needed
      ],
      cart: []
    };
  }

  addToCart = (product) => {
    this.setState({
      cart: [...this.state.cart, product]
    });
  }

  render() {
    return (
      <div className="App">
        <div className="cart">
          <Cart cart={this.state.cart} />
        </div>
        <div className="products">
          {this.state.products.map((product, index) => (
            <Product key={index} product={product} addToCart={this.addToCart} />
          ))}
        </div>
      </div>
    );
  }
}

export default App;
